// function to calculate filters quantity applied
export const filtersQuantityCalculation = (state) => {
  let counter = 0;
  for (let item in state) {
    if (typeof state[item] === 'string') {
      if (state[item] !== '') {
        counter++;
      }
    } else {
      counter += state[item].length;
    }
  }

  return counter;
};

// function to update inputs' state
export function handleFilterOptions(arr, value) {
  if (arr?.includes(value)) {
    return arr.filter((item) => item !== value);
  } else {
    return [...arr, value];
  }
}

// Filter Products function
export function filterProducts(products, state) {
  const result = products.filter((product) => {
    const { tags } = product;
    let productFiltersObj = {};
    let productTypeArray = [];
    let productIngredientArray = [];
    let productConcernArray = [];
    let productCategoryArray = [];

    // routine to filter tags
    for (let tag of tags) {
      if (tag?.toLowerCase().includes('cate')) {
        let category = tag.split(' - ');
        productCategoryArray = [
          ...productCategoryArray,
          category[1]?.toLowerCase(),
        ];
      }

      if (tag?.toLowerCase().includes('type')) {
        let type = tag.split(' - ');
        productTypeArray = [...productTypeArray, type[1]?.toLowerCase()];
      }

      if (tag?.toLowerCase().includes('ingredient')) {
        let ingredient = tag.split(' - ');
        productIngredientArray = [
          ...productIngredientArray,
          ingredient[1]?.toLowerCase(),
        ];
      }

      if (tag?.toLowerCase().includes('concern')) {
        let concern = tag.split(' - ');
        productConcernArray = [
          ...productConcernArray,
          concern[1]?.toLowerCase(),
        ];
      }
    }

    productFiltersObj['skin type'] = productTypeArray;
    productFiltersObj['ingredient preferences'] = productIngredientArray;
    productFiltersObj['skin concern'] = productConcernArray;
    productFiltersObj['product category'] = productCategoryArray;

    return (
      state['product category'].every((elem) =>
        productFiltersObj['product category'].includes(elem.toLowerCase())
      ) &&
      state['skin concern'].every((elem) =>
        productFiltersObj['skin concern'].includes(elem.toLowerCase())
      ) &&
      state['ingredient preferences'].every((elem) =>
        productFiltersObj['ingredient preferences'].includes(elem.toLowerCase())
      ) &&
      state['skin type'].every((elem) =>
        productFiltersObj['skin type'].includes(elem.toLowerCase())
      ) &&
      product
    );
  });

  return result;
}

// Sort Products function
export function sortProducts(sortType, products, filteredProducts) {
  products = products || filteredProducts;
  const clonedObjArray = [...products];

  const sort = {
    'new': () => sendOOSToEnd(clonedObjArray.sort((a, b) => Date.parse(a.createdAt) - Date.parse(b.createdAt)).reverse()),
    'price lo-hi': () => sendOOSToEnd(clonedObjArray.sort((a, b) => getMinPrice(a) - getMinPrice(b))),
    'price hi-lo': () => sendOOSToEnd(clonedObjArray.sort((a, b) => getMinPrice(a) - getMinPrice(b)).reverse()),
    'featured': () => sendOOSToEnd(clonedObjArray),
    'bestselling': () => sendOOSToEnd(clonedObjArray.sort((a, b) => {

      if (!a['sales_rank']) a['sales_rank'] = Math.max() * -1;
      if (!b['sales_rank']) b['sales_rank'] = Math.max() * -1;

      return a['sales_rank'] - b['sales_rank'];

    }))
  };

  function sendOOSToEnd(array) {

    let copyOfproducts = [...array];

    array.forEach((product) => {

      const toSendToBottomOfTheList = product.tags.includes('force_sold_out') || !product.availableForSale;

      if (toSendToBottomOfTheList) {

        const sendToBottom = copyOfproducts.splice(copyOfproducts.indexOf(product), 1)[0];
        copyOfproducts.push(sendToBottom);

      }

    });

    return copyOfproducts;

  }

  function getMinPrice(product){

    return parseFloat(product?.priceRange?.minVariantPrice?.amount)?.toFixed(2);

  }

  return sort[sortType];
}

// Function to get Promo Banners data from collection metafields
export function getBanners(metafields = []) {
  let promos = metafields.filter((o) => o.key.includes('promo'));
  let fireworkStory = metafields.filter((o) => o.key.includes('firework'));

  let newPromos = {};

  promos.forEach((promo) => {
    let ref = promo.namespace.split('.')[0];
    if (Object.prototype.hasOwnProperty.call(newPromos, ref)) {
      newPromos[ref].push(promo);
    } else {
      newPromos[ref] = [promo];
    }
  });

  delete newPromos['global'];

  const newPromosToArray = Object.entries(newPromos);

  if (fireworkStory) newPromosToArray.push(['firework-story', fireworkStory]);

  return newPromosToArray;
}

// Function to get the Promo Banners positions based on screen size
export function changeBannersPosition(size, pos, collectionSize) {
  const getGridPosition = (obj, numColunms, isMobile = false) => {
    let newObj = {};
    for (let position in obj) {
      let row = parseInt(obj[position] / numColunms);
      let colStart = Number(obj[position]) % numColunms;
      row = colStart > 0 ? row + 1 : row;
      colStart = colStart === 0 ? 1 : colStart;
      newObj[position] = {
        gridArea: `${row} / ${colStart} / ${row} / ${isMobile ? 1 : 'span 2'}`,
      };
    }
    return newObj;
  };

  let positions = {};
  if (size.width > 1099) {
    positions = getGridPosition(pos, 4);
    if (collectionSize < pos['pos1'] - 1) {
      positions['pos1'].display = 'none';
      positions['pos2'].display = 'none';
      positions['pos3'].display = 'none';
    } else if (collectionSize < pos['pos2'] - 1) {
      positions['pos2'].display = 'none';
      positions['pos3'].display = 'none';
    } else if (collectionSize < pos['pos3'] - 1) {
      positions['pos3'].display = 'none';
    }
  } else if (size.width > 768) {
    positions = getGridPosition(pos, 3);
    if (collectionSize < pos['pos1'] - 2) {
      positions['pos1'].display = 'none';
      positions['pos2'].display = 'none';
      positions['pos3'].display = 'none';
    } else if (collectionSize < pos['pos2'] - 2) {
      positions['pos2'].display = 'none';
      positions['pos3'].display = 'none';
    } else if (collectionSize < pos['pos3'] - 3) {
      positions['pos3'].display = 'none';
    }
  } else if (size.width > 549) {
    positions = getGridPosition(pos, 2);
    if (collectionSize < pos['pos1'] - 1) {
      positions['pos1'].display = 'none';
      positions['pos2'].display = 'none';
      positions['pos3'].display = 'none';
    } else if (collectionSize < pos['pos2'] - 1) {
      positions['pos1'].height = 'auto';
      positions['pos2'].display = 'none';
      positions['pos3'].display = 'none';
    } else if (collectionSize < pos['pos3'] - 3) {
      positions['pos1'].height = 'auto';
      positions['pos2'].height = 'auto';
      positions['pos3'].display = 'none';
    }
  } else {
    positions = getGridPosition(pos, 1, true);
    if (collectionSize < pos['pos1']) {
      positions['pos1'].display = 'none';
      positions['pos2'].display = 'none';
      positions['pos3'].display = 'none';
    } else if (collectionSize < pos['pos2'] - 1) {
      positions['pos2'].display = 'none';
      positions['pos3'].display = 'none';
    } else if (collectionSize < pos['pos3'] - 1) {
      positions['pos3'].display = 'none';
    }
  }

  return positions;
}

