/* eslint-disable react/no-unknown-property */
import {Link} from '@remix-run/react';
import RebuyRecommendation, {
  links as rebuyRecommendationStyles,
} from '../rebuyRecommendation';
import LoadingSkeleton, {
  links as loadingSkeletonStyles,
} from '~/modules/loadingSkeleton';
import {switchSliderPanelVisibility} from '~/modules/sliderPanel';
import {triggerAnalyticsLoyaltyEvents} from '~/utils/functions/eventFunctions';

import styles from './styles.css';

export const links = () => {
  return [
    {
      rel: 'stylesheet',
      href: styles,
    },
    ...rebuyRecommendationStyles(),
    ...loadingSkeletonStyles(),
  ];
};

export const Slider = () => {
  return (
    <>
      <div
        id="uploadReceipt_slider-divider"
        className={'uploadReceipt_divider'}
      />
      <section className={'uploadReceipt_slider'}>
        <div className={'uploadReceipt_logosContainer'}>
          {Object.values(brandLogos).map((svg, index) => (
            <div key={index}>{svg}</div>
          ))}
        </div>
      </section>

      <div id="slider-divider" className={'uploadReceipt_divider'} />
    </>
  );
};

const UploadReceipt = () => {
  return (
    <div className={'uploadReceipt_mainContainer'}>
      <section className={'uploadReceipt_header'}>
        <p>Earn wherever you shop</p>
        <h1>Upload your receipt</h1>
        <div>
          Simply upload your receipt from Ulta, Sephora, Target or Amazon and
          earn <span>{icons['rounded_star']} 5 points</span> for every $1 spent.
        </div>
      </section>

      <Slider />

      <section className={'uploadReceipt_uploader'}>
        <div
          className="yotpo-widget-instance"
          data-yotpo-instance-id="264193"
          logged-out-store-login-url="/account/login"
          logged-out-store-sign-up-url="/account/register"
        ></div>
        <LoadingSkeleton
          width="45%"
          height="160px"
          style={{maxWidth: '580px', margin: '0 auto', gridArea: '1/1'}}
        />
      </section>

      <section className={'uploadReceipt_cta'}>
        <p>Not a rewards member yet?</p>
        <button
          onClick={() => {
            switchSliderPanelVisibility('SliderAccount');
            triggerAnalyticsLoyaltyEvents('SignupBtnClick', {
              source: 'receiptUploader',
            });
          }}
        >
          Join now
        </button>
        <Link
          onClick={() => {
            triggerAnalyticsLoyaltyEvents('LearnMoreBtnClick', {
              source: 'receiptUploader',
            });
          }}
          to="/rewards"
        >
          Learn more
        </Link>
      </section>

      <section className={'uploadReceipt_recommendations'}>
        <RebuyRecommendation
          widgetId="113756"
          header="ready to earn more points?"
        />
      </section>
    </div>
  );
};

